			DataColumn col9 = dataTable.Columns.Add("DefaultOption", typeof(System.Boolean));
			col9.AllowDBNull = false;		
			DataColumn col10 = dataTable.Columns.Add("Enabled", typeof(System.Boolean));
			col10.AllowDBNull = false;		
			DataColumn col11 = dataTable.Columns.Add("FeatureOptionTypeID", typeof(System.Int32));
			col11.AllowDBNull = true;		
			DataColumn col12 = dataTable.Columns.Add("RegularExpression", typeof(System.String));
			col12.AllowDBNull = true;		
			
			bulkCopy.ColumnMappings.Add("ID", "ID");
			bulkCopy.ColumnMappings.Add("FeatureID", "FeatureID");
			bulkCopy.ColumnMappings.Add("Name", "Name");
			bulkCopy.ColumnMappings.Add("DisplayName", "DisplayName");
			bulkCopy.ColumnMappings.Add("Description", "Description");
			bulkCopy.ColumnMappings.Add("DisplayNameAlt", "DisplayNameAlt");
			bulkCopy.ColumnMappings.Add("DescriptionAlt", "DescriptionAlt");
			bulkCopy.ColumnMappings.Add("Value", "Value");
			bulkCopy.ColumnMappings.Add("DisplayOrder", "DisplayOrder");
			bulkCopy.ColumnMappings.Add("DefaultOption", "DefaultOption");
			bulkCopy.ColumnMappings.Add("Enabled", "Enabled");
			bulkCopy.ColumnMappings.Add("FeatureOptionTypeID", "FeatureOptionTypeID");
			bulkCopy.ColumnMappings.Add("RegularExpression", "RegularExpression");
			
			foreach(CONFDB.Entities.FeatureOption entity in entities)
			{
				if (entity.EntityState != EntityState.Added)
					continue;
					
				DataRow row = dataTable.NewRow();
				
					row["ID"] = entity.Id;
							
				
					row["FeatureID"] = entity.FeatureId;
							
				
					row["Name"] = entity.Name;
							
				
					row["DisplayName"] = entity.DisplayName;
							
				
					row["Description"] = entity.Description;
							
				
					row["DisplayNameAlt"] = entity.DisplayNameAlt;
							
				
					row["DescriptionAlt"] = entity.DescriptionAlt;
							
				
					row["Value"] = entity.Value;
							
				
					row["DisplayOrder"] = entity.DisplayOrder;
							
				
					row["DefaultOption"] = entity.DefaultOption;
							
				
					row["Enabled"] = entity.Enabled;
							
				
					row["FeatureOptionTypeID"] = entity.FeatureOptionTypeId.HasValue ? (object) entity.FeatureOptionTypeId  : System.DBNull.Value;
							
				
					row["RegularExpression"] = entity.RegularExpression;
							
				
				dataTable.Rows.Add(row);
			}		
			
			// send the data to the server		
			bulkCopy.WriteToServer(dataTable);		
			
			// update back the state
			foreach(CONFDB.Entities.FeatureOption entity in entities)
			{
				if (entity.EntityState != EntityState.Added)
					continue;
			
				entity.AcceptChanges();
			}
		}
				
