	public class FeatureComparer : System.Collections.Generic.IComparer<Feature>
	{
		FeatureColumn whichComparison;
		
		/// <summary>
        /// Initializes a new instance of the <see cref="T:FeatureComparer"/> class.
        /// </summary>
		public FeatureComparer()
        {            
        }               
        
        /// <summary>
        /// Initializes a new instance of the <see cref="T:FeatureComparer"/> class.
        /// </summary>
        /// <param name="column">The column to sort on.</param>
        public FeatureComparer(FeatureColumn column)
        {
            this.whichComparison = column;
        }

		/// <summary>
        /// Determines whether the specified <c cref="Feature"/> instances are considered equal.
        /// </summary>
        /// <param name="a">The first <c cref="Feature"/> to compare.</param>
        /// <param name="b">The second <c>Feature</c> to compare.</param>
        /// <returns>true if objA is the same instance as objB or if both are null references or if objA.Equals(objB) returns true; otherwise, false.</returns>
        public bool Equals(Feature a, Feature b)
        {
            return this.Compare(a, b) == 0;
        }

		/// <summary>
        /// Gets the hash code of the specified entity.
        /// </summary>
        /// <param name="entity">The entity.</param>
        /// <returns></returns>
        public int GetHashCode(Feature entity)
        {
            return entity.GetHashCode();
        }

        /// <summary>
        /// Performs a case-insensitive comparison of two objects of the same type and returns a value indicating whether one is less than, equal to, or greater than the other.
        /// </summary>
        /// <param name="a">The first object to compare.</param>
        /// <param name="b">The second object to compare.</param>
        /// <returns></returns>
        public int Compare(Feature a, Feature b)
        {
        	EntityPropertyComparer entityPropertyComparer = new EntityPropertyComparer(this.whichComparison.ToString());
        	return entityPropertyComparer.Compare(a, b);
        }

		/// <summary>
        /// Gets or sets the column that will be used for comparison.
        /// </summary>
        /// <value>The comparison column.</value>
